$(document).ready(function(){
	//general for forms
	//$(':input').css('min-width','90px');
	//$('form select').chosen({ allow_single_deselect:true});
	$('form select').chosen();
});
jQuery(function($) {
	//Image upload 
	$('#uploadimage').ace_file_input({
		no_file:'No File ...',
		btn_choose:'Choose',
		btn_change:'Change',
		droppable:false,
		onchange:null,
		thumbnail: 'small',
		whitelist:'gif|png|jpg|jpeg',
		//blacklist:'exe|php'
		//onchange:''
	});
	//file upload 
	$('#uploadfile').ace_file_input({
		no_file:'No File ...',
		btn_choose:'Choose',
		btn_change:'Change',
		droppable:false,
		onchange:null,
		thumbnail: 'small',
		whitelist:'doc|docx|pdf',
		//blacklist:'exe|php'
		//onchange:''
	});		
	$('#uploadimage_drag_drop').ace_file_input({
		style:'well',
		btn_choose:'Drop Image here or click to choose',
		btn_change:null,
		no_icon:'ace-icon fa fa-cloud-upload',
		droppable:true,
		thumbnail:'fit',//large | small
		whitelist:"jpeg|jpg|png|gif",
		preview_error : function(filename, error_code) {		}
	});
	
	//for modal
	$('body').on('hidden.bs.modal', '.modal', function () {
        $(this).removeData('bs.modal');
    });    
    //tooltip
	//$('[data-tooltip="tooltip"]').tooltip()
	//Date range picker
	$('.input-daterange').datepicker({autoclose: true});
	
	// chosen read only	
	var select = $('form select');
	select.each( function(){
		$(this).on('chosen:updated', function () {
			if ($(this).attr('readonly')) { 
				var wasDisabled = select.is(':disabled'); 
				$(this).attr('disabled', 'disabled'); 
				$(this).data('chosen').search_field_disabled(); 
				if (wasDisabled) { 
					$(this).attr('disabled', 'disabled'); 
				} else { 
					$(this).removeAttr('disabled'); 
				} 
			} 
		}); 
	}); 
	select.trigger('chosen:updated');
	
	//jscrollpane div
	$('#jscroll_div').jScrollPane();
	
});
